
var fs = require( 'fs' );

var data = fs.readFileSync( '././info.txt', 'UTF8' );

function get_info( id ) {

    var str = "^" + id + "#(.*)#(.*\\s*\\S*.*)" +
    "#(.*)#(.*)#(.*)#(.*)#(.*)#(.*)#(.*)#(.*)#(.*)[$](.*)[$](.*)\\s*\\S*" +
    "[$](.*)[$](.*)[$](.*)[$](.*)[$](.*)@(.*\\s*\\S*.*)" +
    "[$](.*)[$](.*)[$](.*)[$](.*)[$](.*)[$](.*)[$](.*)@(.*)[$](.*)[$](.*)[$](.*)[$](.*)\\s*\\S*" +
    "[$](.*)[$](.*)[$](.*)@(.*)[$](.*)[$](.*)[$](.*)[$](.*)[$](.*)[$](.*)[$](.*)" +
    "";

    var reg = new RegExp( str, "mi" );

    return data.match( reg );
}

module.exports.get_info = get_info;
